import Image from "next/image";
import Link from "next/link";
import React, { useState } from "react";
import { RiHeartFill } from "react-icons/ri";
import { VscHeart } from "react-icons/vsc";

const Product = ({ product, addFavProduct, favourites }) => {
  const {
    id,
    uid,
    data: { image, title, price, hover },
  } = product;

  return (
    <article className="cursor-pointer relative group">
      <div
        className={`hidden grid-cols-1 items-center gap-2  absolute bottom-10 right-5 z-[10]
      ${hover.url ? "group-hover:grid" : ""} 
      `}
      >
        <div className="rounded-2xl w-3 h-3  bg-[#fb6d19] "></div>
        <div className="rounded-2xl w-3 h-3 bg-[#9d532c] "></div>
        <div className="rounded-2xl w-3 h-3 bg-black"></div>
        <div className="rounded-2xl w-3 h-3 bg-[#2e3f57] "></div>
        <div className=" ml-[-8px] ">+10</div>
      </div>
      <div className="article overflow-hidden relative ">
        <div className="absolute top-5 right-5 z-[10] hidden group-hover:block ">
          {favourites?.some((item) => item.id === id) ? (
            <RiHeartFill onClick={() => addFavProduct(product, title)} />
          ) : (
            <VscHeart onClick={() => addFavProduct(product, title)} />
          )}
        </div>
        <Link href={"/products/" + uid}>
          <a className="bg-red-300">
            <Image
              className={`absolute z-10 ${
                hover?.url
                  ? "group-hover:opacity-0 group-hover:z-0"
                  : "group-hover:z-0"
              }  `}
              src={image?.url}
              alt=""
              layout="fill"
              objectFit="cover"
              placeholder="blur"
              blurDataURL="data:image/jpeg;base64,"
            />
            {hover?.url && (
              <Image
                className={`absolute z-0`}
                src={hover.url}
                alt=""
                layout="fill"
                objectFit="cover"
                placeholder="blur"
                blurDataURL="data:image/jpeg;base64,"
              />
            )}
          </a>
        </Link>
      </div>
      <div className="mt-[6px] grid grid-cols-2">
        <h2>{title}</h2>
        <h2>${price}</h2>
      </div>
    </article>
  );
};

export default Product;
